import { GoogleMap, useLoadScript, MarkerF } from "@react-google-maps/api";
import { useMemo } from "react";
import { useState } from "react";
import css from './App.css';
import axios from "axios";

export default function App() {
  const [city, setCity] = useState('');
  const [coordonates, setCoordonates] = useState();

  const { isLoaded } = useLoadScript({
    googleMapsApiKey : 'AIzaSyDABRdbH02GKNiu2LVWLsphYdvskRDVRzI',
  })

  const searchCity = async () => {
    const gps = [];
    const url = 'https://api.roadgoat.com/api/v2/destinations/auto_complete?q=' + city
    const data = await axios.get(url, {
      auth : {
        username: '15129d74d644ac186207f1149eab0762',
        password:'0261b66fe76ad30b05e2f2ff0080da94'
      }
    }).then((res) => {
      res.data.data.map(ville => {
      gps.push({latitude: ville.attributes.latitude, longitude: ville.attributes.longitude}) 
      }
    )
  }
  )
  console.log(gps)
  setCoordonates(gps)
}

  if (!isLoaded) return <div>Loading...</div>

  return (
    <>
    <Map data={coordonates}/>
    <div>
    <input className="border border-black" type="text" value={city} onChange={(event) => {setCity(event.target.value)}}/>
    <button onClick={searchCity} className="border border-black">Search City</button>
    </div>
    </>
    );
}

function Map({data}) {
  //const center = useMemo(()=>({lat:48, lng: 2}), []);
  return (
    <>
    <GoogleMap zoom={3} center={{ lat: 48.85341, lng: 2.3488 }} mapContainerClassName="map-container">
    <Markers obj={data}/>
    </GoogleMap>
    </>
  );

}

function Markers({obj}) {

  let result = obj.map( (city) => {

    return <MarkerF position={{lat: city.latitude, lng:city.longitude}}/>
  });

  return (
    <>
    {result}
    </>
  )
}
