import { GoogleMap, useLoadScript, MarkerF } from "@react-google-maps/api";
import { useState, useEffect } from "react";
import css from './App.css';
import axios from "axios";
import Map from "./components/Map";
import Result from "./components/Result";
import Form from "./components/Form";
import Title from "./components/Title";


export default function App() {
  const [city, setCity] = useState('');
  const [coordonates, setCoordonates] = useState([]);
  const [town, setTown] = useState('');
  const [countries, setCountries] = useState();





  const searchCity = async () => {

    const url = 'https://api.roadgoat.com/api/v2/destinations/auto_complete?q=' + city
    await axios.get(url, {
      auth : {
        username: process.env.REACT_APP_ROAD_GOAT_USERNAME,
        password: process.env.REACT_APP_ROAD_GOAT_PASSWORD
      }
    }).then((res) => {
      console.log(res.data.data)
      const gps = [];
      res.data.data.map(ville => {
       if (ville.attributes.short_name.toLowerCase() == city){
        gps.push({short: ville.attributes.short_name, name: ville.attributes.long_name, latitude: ville.attributes.latitude, longitude: ville.attributes.longitude})
       }
      })
      setCoordonates(gps);

      const ville = coordonates[0].short
      setTown(ville)
      console.log('city = ', town)
      console.log("countries = ", countries)

  });

  }

  const countryList =  () => {

    let countries = [];
    coordonates.map((ville) => {
        const arr = ville.name.split(',');
        const country = arr[arr.length -1];
        if (countries.includes(country) === false){
          countries.push(country);
        }
    });

    let result = countries.map((country) => {
      return <div className="flex space-x-4" key={country}>
              <h2>{country}</h2>
            </div>
    });
    console.log('result = ', result)
    setCountries(result)
  }

  return (
    <>
    <div className="flex flex-col space-y-4 items-center">
      <Title />
      <Form city={city} 
      searchCity={()=>{
        searchCity().then(countryList())}} 
        setCity={setCity}/>
      <div className="border border-black w-9/12">
      <Map data={coordonates}/>
      </div>
      <Result city={town} countries={countries}/>
    </div>
    </>
    );
}
