import { GoogleMap, useLoadScript, MarkerF } from "@react-google-maps/api";
import { useMemo } from "react";
import { useState } from "react";
import css from './App.css';
import axios from "axios";
const BASE_URL = process.env.REACT_APP_BASE_URL;


export default function App() {
  const [city, setCity] = useState('');
  const [coordonates, setCoordonates] = useState([]);

  const { isLoaded } = useLoadScript({
    googleMapsApiKey : "AIzaSyDABRdbH02GKNiu2LVWLsphYdvskRDVRzI",
  })

  const searchCity = async () => {
    const gps = [];
    const url = 'https://api.roadgoat.com/api/v2/destinations/auto_complete?q=' + city
    const data = await axios.get(url, {
      auth : {
        username: process.env.ROAD_GOAT_USERNAME,
        password: process.env.ROAD_GOAT_PASSWORD
      }
    }).then((res) => {
      console.log(res.data.data)
      res.data.data.map(ville => {
       if (ville.attributes.short_name.toLowerCase() == city){
        gps.push({name: ville.attributes.long_name, latitude: ville.attributes.latitude, longitude: ville.attributes.longitude})
       }
      }
    )
  }
  )
  setCoordonates(gps)

}

  let displayNames = coordonates.map((ville) => {
    const arr = ville.name.split(',');
    return <div className="flex space-x-4">
      <h1>{arr[0]}</h1>
      <h2>Country : {arr[arr.length -1]}</h2>
      </div>
  })


  if (!isLoaded) return <div>Loading...</div>

  return (
    <>
    <div className="flex flex-col space-y-8">
      <div className="flex items-center justify-center space-x-4 pt-8">
        <input className="border border-black" type="text" value={city} onChange={(event) => {setCity(event.target.value)}}/>
        <button onClick={searchCity} className="border border-black">Search City</button>
      </div>
      <div>{displayNames}</div>
      <div>
      <Map data={coordonates}/>
      </div>
    </div>
    </>
    );
}

function Map({data}) {
  //const center = useMemo(()=>({lat:48, lng: 2}), []);
  return (
    <>
    <GoogleMap zoom={2} center={{ lat: 48.85341, lng: 2.3488 }} mapContainerClassName="map-container">
    <Markers obj={data}/>
    </GoogleMap>
    </>
  );

}

function Markers({obj}) {

  let result = obj.map( (city) => {
    return <MarkerF position={{lat: city.latitude, lng:city.longitude}}/>
  });

  return (
    <>
    {result}
    </>
  )
}
